#!/bin/bash
# Uninstall script for Airfoil
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Airfoil..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Airfoil if running..."
pkill -f "Airfoil" 2>/dev/null || true

# Remove /Applications/Airfoil/Airfoil Satellite.app
echo "Removing /Applications/Airfoil/Airfoil Satellite.app..."
if [ -d "/Applications/Airfoil/Airfoil Satellite.app" ]; then
    rm -rf "/Applications/Airfoil/Airfoil Satellite.app" 2>/dev/null || true
elif [ -f "/Applications/Airfoil/Airfoil Satellite.app" ]; then
    rm -f "/Applications/Airfoil/Airfoil Satellite.app" 2>/dev/null || true
fi

# Remove /Applications/Airfoil/Airfoil.app
echo "Removing /Applications/Airfoil/Airfoil.app..."
if [ -d "/Applications/Airfoil/Airfoil.app" ]; then
    rm -rf "/Applications/Airfoil/Airfoil.app" 2>/dev/null || true
elif [ -f "/Applications/Airfoil/Airfoil.app" ]; then
    rm -f "/Applications/Airfoil/Airfoil.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
