#!/bin/bash
# Uninstall script for Angry IP Scanner
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Angry IP Scanner..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Angry IP Scanner if running..."
pkill -f "Angry IP Scanner" 2>/dev/null || true

# Remove /Applications/Angry IP Scanner.app
echo "Removing /Applications/Angry IP Scanner.app..."
if [ -d "/Applications/Angry IP Scanner.app" ]; then
    rm -rf "/Applications/Angry IP Scanner.app" 2>/dev/null || true
elif [ -f "/Applications/Angry IP Scanner.app" ]; then
    rm -f "/Applications/Angry IP Scanner.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
