#!/bin/bash
# Uninstall script for Anki
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Anki..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Anki if running..."
pkill -f "Anki" 2>/dev/null || true

# Remove /Applications/Anki.app
echo "Removing /Applications/Anki.app..."
if [ -d "/Applications/Anki.app" ]; then
    rm -rf "/Applications/Anki.app" 2>/dev/null || true
elif [ -f "/Applications/Anki.app" ]; then
    rm -f "/Applications/Anki.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Anki*
echo "Removing $HOME/Library/Application Support/Anki*..."
if [ -d "$HOME/Library/Application Support/Anki*" ]; then
    rm -rf "$HOME/Library/Application Support/Anki*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Anki*" ]; then
    rm -f "$HOME/Library/Application Support/Anki*" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Anki
echo "Removing $HOME/Library/Caches/Anki..."
if [ -d "$HOME/Library/Caches/Anki" ]; then
    rm -rf "$HOME/Library/Caches/Anki" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Anki" ]; then
    rm -f "$HOME/Library/Caches/Anki" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/net.ankiweb.dtop.savedState
echo "Removing $HOME/Library/Saved Application State/net.ankiweb.dtop.savedState..."
if [ -d "$HOME/Library/Saved Application State/net.ankiweb.dtop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/net.ankiweb.dtop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/net.ankiweb.dtop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/net.ankiweb.dtop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
