#!/bin/bash
# Uninstall script for Apidog
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Apidog..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Apidog if running..."
pkill -f "Apidog" 2>/dev/null || true

# Remove /Applications/Apidog.app
echo "Removing /Applications/Apidog.app..."
if [ -d "/Applications/Apidog.app" ]; then
    rm -rf "/Applications/Apidog.app" 2>/dev/null || true
elif [ -f "/Applications/Apidog.app" ]; then
    rm -f "/Applications/Apidog.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/apidog
echo "Removing $HOME/Library/Application Support/apidog..."
if [ -d "$HOME/Library/Application Support/apidog" ]; then
    rm -rf "$HOME/Library/Application Support/apidog" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/apidog" ]; then
    rm -f "$HOME/Library/Application Support/apidog" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.apidog.app.plist
echo "Removing $HOME/Library/Preferences/com.apidog.app.plist..."
if [ -d "$HOME/Library/Preferences/com.apidog.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.apidog.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.apidog.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.apidog.app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.apidog.app.savedState
echo "Removing $HOME/Library/Saved Application State/com.apidog.app.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.apidog.app.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.apidog.app.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.apidog.app.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.apidog.app.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
