#!/bin/bash
# Uninstall script for Asana
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Asana..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Asana if running..."
pkill -f "Asana" 2>/dev/null || true

# Remove /Applications/Asana.app
echo "Removing /Applications/Asana.app..."
if [ -d "/Applications/Asana.app" ]; then
    rm -rf "/Applications/Asana.app" 2>/dev/null || true
elif [ -f "/Applications/Asana.app" ]; then
    rm -f "/Applications/Asana.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Asana
echo "Removing $HOME/Library/Application Support/Asana..."
if [ -d "$HOME/Library/Application Support/Asana" ]; then
    rm -rf "$HOME/Library/Application Support/Asana" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Asana" ]; then
    rm -f "$HOME/Library/Application Support/Asana" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.electron.asana
echo "Removing $HOME/Library/Caches/com.electron.asana..."
if [ -d "$HOME/Library/Caches/com.electron.asana" ]; then
    rm -rf "$HOME/Library/Caches/com.electron.asana" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.electron.asana" ]; then
