fi

# Remove $HOME/Library/Preferences/com.electron.asana.plist
echo "Removing $HOME/Library/Preferences/com.electron.asana.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.asana.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.asana.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.asana.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.asana.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.electron.asana.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.asana.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.asana.savedState" ]; then
