#!/bin/bash
# Uninstall script for Azure Data Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Azure Data Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Azure Data Studio if running..."
pkill -f "Azure Data Studio" 2>/dev/null || true

# Remove /Applications/Azure Data Studio.app
echo "Removing /Applications/Azure Data Studio.app..."
if [ -d "/Applications/Azure Data Studio.app" ]; then
    rm -rf "/Applications/Azure Data Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Azure Data Studio.app" ]; then
    rm -f "/Applications/Azure Data Studio.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
