#!/bin/bash
# Uninstall script for Background Music
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Background Music..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Background Music if running..."
pkill -f "Background Music" 2>/dev/null || true

# Unload service com.bearisdriving.BGM.XPCHelper
echo "Unloading service com.bearisdriving.BGM.XPCHelper..."
launchctl unload -w /Library/LaunchAgents/com.bearisdriving.BGM.XPCHelper.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.bearisdriving.BGM.XPCHelper.plist 2>/dev/null || true

# Kill application with bundle ID com.bearisdriving.BGM.App if running
echo "Stopping application with bundle ID com.bearisdriving.BGM.App if running..."
killall -9 "com.bearisdriving.BGM.App" 2>/dev/null || true

# Remove /Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist
echo "Removing /Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist..."
if [ -d "/Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist" ]; then
    rm -rf "/Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist" 2>/dev/null || true
elif [ -f "/Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist" ]; then
    rm -f "/Library/LaunchDaemons/com.bearisdriving.BGM.XPCHelper.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.bearisdriving.BGM.App.plist
echo "Removing $HOME/Library/Preferences/com.bearisdriving.BGM.App.plist..."
if [ -d "$HOME/Library/Preferences/com.bearisdriving.BGM.App.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.bearisdriving.BGM.App.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.bearisdriving.BGM.App.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.bearisdriving.BGM.App.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
