#!/bin/bash
# Uninstall script for Basecamp
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Basecamp..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Basecamp if running..."
pkill -f "Basecamp" 2>/dev/null || true

# Remove /Applications/Basecamp.app
echo "Removing /Applications/Basecamp.app..."
if [ -d "/Applications/Basecamp.app" ]; then
    rm -rf "/Applications/Basecamp.app" 2>/dev/null || true
elif [ -f "/Applications/Basecamp.app" ]; then
    rm -f "/Applications/Basecamp.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Basecamp*
echo "Removing $HOME/Library/Application Support/Basecamp*..."
if [ -d "$HOME/Library/Application Support/Basecamp*" ]; then
    rm -rf "$HOME/Library/Application Support/Basecamp*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Basecamp*" ]; then
    rm -f "$HOME/Library/Application Support/Basecamp*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.basecamp.basecamp*.plist
echo "Removing $HOME/Library/Preferences/com.basecamp.basecamp*.plist..."
if [ -d "$HOME/Library/Preferences/com.basecamp.basecamp*.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.basecamp.basecamp*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.basecamp.basecamp*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.basecamp.basecamp*.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState
echo "Removing $HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.basecamp.basecamp*.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
