#!/bin/bash
# Uninstall script for BetterMouse
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BetterMouse..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BetterMouse if running..."
pkill -f "BetterMouse" 2>/dev/null || true

# Remove /Applications/BetterMouse.app
echo "Removing /Applications/BetterMouse.app..."
if [ -d "/Applications/BetterMouse.app" ]; then
    rm -rf "/Applications/BetterMouse.app" 2>/dev/null || true
elif [ -f "/Applications/BetterMouse.app" ]; then
    rm -f "/Applications/BetterMouse.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
