#!/bin/bash
# Uninstall script for BetterTouchTool
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BetterTouchTool..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BetterTouchTool if running..."
pkill -f "BetterTouchTool" 2>/dev/null || true

# Kill application with bundle ID com.hegenberg.BetterTouchTool if running
echo "Stopping application with bundle ID com.hegenberg.BetterTouchTool if running..."
killall -9 "com.hegenberg.BetterTouchTool" 2>/dev/null || true

# Remove /Applications/BetterTouchTool.app
echo "Removing /Applications/BetterTouchTool.app..."
if [ -d "/Applications/BetterTouchTool.app" ]; then
    rm -rf "/Applications/BetterTouchTool.app" 2>/dev/null || true
elif [ -f "/Applications/BetterTouchTool.app" ]; then
    rm -f "/Applications/BetterTouchTool.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/BetterTouchTool
echo "Removing $HOME/Library/Application Support/BetterTouchTool..."
if [ -d "$HOME/Library/Application Support/BetterTouchTool" ]; then
    rm -rf "$HOME/Library/Application Support/BetterTouchTool" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/BetterTouchTool" ]; then
    rm -f "$HOME/Library/Application Support/BetterTouchTool" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist
echo "Removing $HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist..."
if [ -d "$HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.hegenberg.BetterTouchTool.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
