#!/bin/bash
# Uninstall script for BetterZip
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling BetterZip..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping BetterZip if running..."
pkill -f "BetterZip" 2>/dev/null || true

# Remove /Applications/BetterZip.app
echo "Removing /Applications/BetterZip.app..."
if [ -d "/Applications/BetterZip.app" ]; then
    rm -rf "/Applications/BetterZip.app" 2>/dev/null || true
elif [ -f "/Applications/BetterZip.app" ]; then
    rm -f "/Applications/BetterZip.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
