#!/bin/bash
# Uninstall script for Beyond Compare
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Beyond Compare..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Beyond Compare if running..."
pkill -f "Beyond Compare" 2>/dev/null || true

# Remove /Applications/Beyond Compare.app
echo "Removing /Applications/Beyond Compare.app..."
if [ -d "/Applications/Beyond Compare.app" ]; then
    rm -rf "/Applications/Beyond Compare.app" 2>/dev/null || true
elif [ -f "/Applications/Beyond Compare.app" ]; then
    rm -f "/Applications/Beyond Compare.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
