#!/bin/bash
# Uninstall script for blip
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling blip..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping blip if running..."
pkill -f "blip" 2>/dev/null || true

# Remove /Applications/Blip.app
echo "Removing /Applications/Blip.app..."
if [ -d "/Applications/Blip.app" ]; then
    rm -rf "/Applications/Blip.app" 2>/dev/null || true
elif [ -f "/Applications/Blip.app" ]; then
    rm -f "/Applications/Blip.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
