#!/bin/bash
# Uninstall script for Boop
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Boop..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Boop if running..."
pkill -f "Boop" 2>/dev/null || true

# Remove /Applications/Boop.app
echo "Removing /Applications/Boop.app..."
if [ -d "/Applications/Boop.app" ]; then
    rm -rf "/Applications/Boop.app" 2>/dev/null || true
elif [ -f "/Applications/Boop.app" ]; then
    rm -f "/Applications/Boop.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.okatbest.boop
echo "Removing $HOME/Library/Application Scripts/com.okatbest.boop..."
if [ -d "$HOME/Library/Application Scripts/com.okatbest.boop" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.okatbest.boop" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.okatbest.boop" ]; then
    rm -f "$HOME/Library/Application Scripts/com.okatbest.boop" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.okatbest.boop
echo "Removing $HOME/Library/Containers/com.okatbest.boop..."
if [ -d "$HOME/Library/Containers/com.okatbest.boop" ]; then
    rm -rf "$HOME/Library/Containers/com.okatbest.boop" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.okatbest.boop" ]; then
    rm -f "$HOME/Library/Containers/com.okatbest.boop" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
