#!/bin/bash
# Uninstall script for Bruno
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bruno..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bruno if running..."
pkill -f "Bruno" 2>/dev/null || true

# Remove /Applications/Bruno.app
echo "Removing /Applications/Bruno.app..."
if [ -d "/Applications/Bruno.app" ]; then
    rm -rf "/Applications/Bruno.app" 2>/dev/null || true
elif [ -f "/Applications/Bruno.app" ]; then
    rm -f "/Applications/Bruno.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/bruno
echo "Removing $HOME/Library/Application Support/bruno..."
if [ -d "$HOME/Library/Application Support/bruno" ]; then
    rm -rf "$HOME/Library/Application Support/bruno" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/bruno" ]; then
    rm -f "$HOME/Library/Application Support/bruno" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.usebruno.app.plist
echo "Removing $HOME/Library/Preferences/com.usebruno.app.plist..."
if [ -d "$HOME/Library/Preferences/com.usebruno.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.usebruno.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.usebruno.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.usebruno.app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.usebruno.app.savedState
echo "Removing $HOME/Library/Saved Application State/com.usebruno.app.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.usebruno.app.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.usebruno.app.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.usebruno.app.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.usebruno.app.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
