#!/bin/bash
# Uninstall script for Caffeine
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Caffeine..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Caffeine if running..."
pkill -f "Caffeine" 2>/dev/null || true

# Remove /Applications/Caffeine.app
echo "Removing /Applications/Caffeine.app..."
if [ -d "/Applications/Caffeine.app" ]; then
    rm -rf "/Applications/Caffeine.app" 2>/dev/null || true
elif [ -f "/Applications/Caffeine.app" ]; then
    rm -f "/Applications/Caffeine.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
