#!/bin/bash
# Uninstall script for Calmly Writer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Calmly Writer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Calmly Writer if running..."
pkill -f "Calmly Writer" 2>/dev/null || true

# Remove /Applications/Calmly Writer.app
echo "Removing /Applications/Calmly Writer.app..."
if [ -d "/Applications/Calmly Writer.app" ]; then
    rm -rf "/Applications/Calmly Writer.app" 2>/dev/null || true
elif [ -f "/Applications/Calmly Writer.app" ]; then
    rm -f "/Applications/Calmly Writer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/calmlywriter.plist
echo "Removing $HOME/Library/Preferences/calmlywriter.plist..."
if [ -d "$HOME/Library/Preferences/calmlywriter.plist" ]; then
    rm -rf "$HOME/Library/Preferences/calmlywriter.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/calmlywriter.plist" ]; then
    rm -f "$HOME/Library/Preferences/calmlywriter.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/calmlywriter.savedState
echo "Removing $HOME/Library/Saved Application State/calmlywriter.savedState..."
if [ -d "$HOME/Library/Saved Application State/calmlywriter.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/calmlywriter.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/calmlywriter.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/calmlywriter.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
