#!/bin/bash
# Uninstall script for Cisco Jabber
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cisco Jabber..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cisco Jabber if running..."
pkill -f "Cisco Jabber" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Cisco/Unified Communications/Jabber
echo "Removing $HOME/Library/Application Support/Cisco/Unified Communications/Jabber..."
if [ -d "$HOME/Library/Application Support/Cisco/Unified Communications/Jabber" ]; then
    rm -rf "$HOME/Library/Application Support/Cisco/Unified Communications/Jabber" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Cisco/Unified Communications/Jabber" ]; then
    rm -f "$HOME/Library/Application Support/Cisco/Unified Communications/Jabber" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.cisco.Jabber
echo "Removing $HOME/Library/Caches/com.cisco.Jabber..."
if [ -d "$HOME/Library/Caches/com.cisco.Jabber" ]; then
    rm -rf "$HOME/Library/Caches/com.cisco.Jabber" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.cisco.Jabber" ]; then
    rm -f "$HOME/Library/Caches/com.cisco.Jabber" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Jabber
echo "Removing $HOME/Library/Logs/Jabber..."
if [ -d "$HOME/Library/Logs/Jabber" ]; then
    rm -rf "$HOME/Library/Logs/Jabber" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Jabber" ]; then
    rm -f "$HOME/Library/Logs/Jabber" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.cisco.Jabber.plist
echo "Removing $HOME/Library/Preferences/com.cisco.Jabber.plist..."
if [ -d "$HOME/Library/Preferences/com.cisco.Jabber.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.cisco.Jabber.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.cisco.Jabber.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.cisco.Jabber.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/WebKit/com.cisco.Jabber
echo "Removing $HOME/Library/WebKit/com.cisco.Jabber..."
if [ -d "$HOME/Library/WebKit/com.cisco.Jabber" ]; then
    rm -rf "$HOME/Library/WebKit/com.cisco.Jabber" 2>/dev/null || true
elif [ -f "$HOME/Library/WebKit/com.cisco.Jabber" ]; then
    rm -f "$HOME/Library/WebKit/com.cisco.Jabber" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
