#!/bin/bash
# Uninstall script for Claude
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Claude..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Claude if running..."
pkill -f "Claude" 2>/dev/null || true

# Remove /Applications/Claude.app
echo "Removing /Applications/Claude.app..."
if [ -d "/Applications/Claude.app" ]; then
    rm -rf "/Applications/Claude.app" 2>/dev/null || true
elif [ -f "/Applications/Claude.app" ]; then
    rm -f "/Applications/Claude.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
