#!/bin/bash
# Uninstall script for CodeRunner
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CodeRunner..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CodeRunner if running..."
pkill -f "CodeRunner" 2>/dev/null || true

# Remove /Applications/CodeRunner.app
echo "Removing /Applications/CodeRunner.app..."
if [ -d "/Applications/CodeRunner.app" ]; then
    rm -rf "/Applications/CodeRunner.app" 2>/dev/null || true
elif [ -f "/Applications/CodeRunner.app" ]; then
    rm -f "/Applications/CodeRunner.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
