# Remove /Applications/CopyQ.app
echo "Removing /Applications/CopyQ.app..."
if [ -d "/Applications/CopyQ.app" ]; then
    rm -rf "/Applications/CopyQ.app" 2>/dev/null || true
elif [ -f "/Applications/CopyQ.app" ]; then
    rm -f "/Applications/CopyQ.app" 2>/dev/null || true
fi

