    rm -f "$HOME/.config/copyq" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/copyq
echo "Removing $HOME/Library/Application Support/copyq..."
if [ -d "$HOME/Library/Application Support/copyq" ]; then
    rm -rf "$HOME/Library/Application Support/copyq" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/copyq" ]; then
    rm -f "$HOME/Library/Application Support/copyq" 2>/dev/null || true
