#!/bin/bash
# Uninstall script for CrashPlan
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling CrashPlan..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping CrashPlan if running..."
pkill -f "CrashPlan" 2>/dev/null || true

# Unload service com.crashplan.engine
echo "Unloading service com.crashplan.engine..."
launchctl unload -w /Library/LaunchAgents/com.crashplan.engine.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.crashplan.engine.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.crashplan.engine.plist 2>/dev/null || true

# Kill application with bundle ID com.crashplan.app if running
echo "Stopping application with bundle ID com.crashplan.app if running..."
killall -9 "com.crashplan.app" 2>/dev/null || true

# Remove $HOME/Library/Application Support/CrashPlan
echo "Removing $HOME/Library/Application Support/CrashPlan..."
if [ -d "$HOME/Library/Application Support/CrashPlan" ]; then
    rm -rf "$HOME/Library/Application Support/CrashPlan" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/CrashPlan" ]; then
    rm -f "$HOME/Library/Application Support/CrashPlan" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.crashplan.desktop.plist
echo "Removing $HOME/Library/Preferences/com.crashplan.desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.crashplan.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.crashplan.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.crashplan.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.crashplan.desktop.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
