#!/bin/bash
# Uninstall script for Cryptomator
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cryptomator..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cryptomator if running..."
pkill -f "Cryptomator" 2>/dev/null || true

# Remove /Applications/Cryptomator.app
echo "Removing /Applications/Cryptomator.app..."
if [ -d "/Applications/Cryptomator.app" ]; then
    rm -rf "/Applications/Cryptomator.app" 2>/dev/null || true
elif [ -f "/Applications/Cryptomator.app" ]; then
    rm -f "/Applications/Cryptomator.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Cryptomator
echo "Removing $HOME/Library/Application Support/Cryptomator..."
if [ -d "$HOME/Library/Application Support/Cryptomator" ]; then
    rm -rf "$HOME/Library/Application Support/Cryptomator" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Cryptomator" ]; then
    rm -f "$HOME/Library/Application Support/Cryptomator" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Cryptomator
echo "Removing $HOME/Library/Logs/Cryptomator..."
if [ -d "$HOME/Library/Logs/Cryptomator" ]; then
    rm -rf "$HOME/Library/Logs/Cryptomator" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Cryptomator" ]; then
    rm -f "$HOME/Library/Logs/Cryptomator" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.cryptomator.plist
echo "Removing $HOME/Library/Preferences/org.cryptomator.plist..."
if [ -d "$HOME/Library/Preferences/org.cryptomator.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.cryptomator.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.cryptomator.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.cryptomator.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
