#!/bin/bash
# Uninstall script for Crystalfetch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Crystalfetch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Crystalfetch if running..."
pkill -f "Crystalfetch" 2>/dev/null || true

# Remove /Applications/CrystalFetch.app
echo "Removing /Applications/CrystalFetch.app..."
if [ -d "/Applications/CrystalFetch.app" ]; then
    rm -rf "/Applications/CrystalFetch.app" 2>/dev/null || true
elif [ -f "/Applications/CrystalFetch.app" ]; then
    rm -f "/Applications/CrystalFetch.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/llc.turing.CrystalFetch
echo "Removing $HOME/Library/Application Scripts/llc.turing.CrystalFetch..."
if [ -d "$HOME/Library/Application Scripts/llc.turing.CrystalFetch" ]; then
    rm -rf "$HOME/Library/Application Scripts/llc.turing.CrystalFetch" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/llc.turing.CrystalFetch" ]; then
    rm -f "$HOME/Library/Application Scripts/llc.turing.CrystalFetch" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/llc.turing.CrystalFetch
echo "Removing $HOME/Library/Containers/llc.turing.CrystalFetch..."
if [ -d "$HOME/Library/Containers/llc.turing.CrystalFetch" ]; then
    rm -rf "$HOME/Library/Containers/llc.turing.CrystalFetch" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/llc.turing.CrystalFetch" ]; then
    rm -f "$HOME/Library/Containers/llc.turing.CrystalFetch" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
