#!/bin/bash
# Uninstall script for Cursor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Cursor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Cursor if running..."
pkill -f "Cursor" 2>/dev/null || true

# Remove /Applications/Cursor.app
echo "Removing /Applications/Cursor.app..."
if [ -d "/Applications/Cursor.app" ]; then
    rm -rf "/Applications/Cursor.app" 2>/dev/null || true
elif [ -f "/Applications/Cursor.app" ]; then
    rm -f "/Applications/Cursor.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
