#!/bin/bash
# Uninstall script for DevToys
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DevToys..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DevToys if running..."
pkill -f "DevToys" 2>/dev/null || true

# Remove /Applications/DevToys.app
echo "Removing /Applications/DevToys.app..."
if [ -d "/Applications/DevToys.app" ]; then
    rm -rf "/Applications/DevToys.app" 2>/dev/null || true
elif [ -f "/Applications/DevToys.app" ]; then
    rm -f "/Applications/DevToys.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
