#!/bin/bash
# Uninstall script for Doughnut
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Doughnut..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Doughnut if running..."
pkill -f "Doughnut" 2>/dev/null || true

# Remove /Applications/Doughnut.app
echo "Removing /Applications/Doughnut.app..."
if [ -d "/Applications/Doughnut.app" ]; then
    rm -rf "/Applications/Doughnut.app" 2>/dev/null || true
elif [ -f "/Applications/Doughnut.app" ]; then
    rm -f "/Applications/Doughnut.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.cdyer.doughnut.plist
echo "Removing $HOME/Library/Preferences/com.cdyer.doughnut.plist..."
if [ -d "$HOME/Library/Preferences/com.cdyer.doughnut.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.cdyer.doughnut.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.cdyer.doughnut.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.cdyer.doughnut.plist" 2>/dev/null || true
fi

# Remove $HOME/Music/Doughnut
echo "Removing $HOME/Music/Doughnut..."
if [ -d "$HOME/Music/Doughnut" ]; then
    rm -rf "$HOME/Music/Doughnut" 2>/dev/null || true
elif [ -f "$HOME/Music/Doughnut" ]; then
    rm -f "$HOME/Music/Doughnut" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
