#!/bin/bash
# Uninstall script for DrawBot
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DrawBot..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DrawBot if running..."
pkill -f "DrawBot" 2>/dev/null || true

# Remove /Applications/DrawBot.app
echo "Removing /Applications/DrawBot.app..."
if [ -d "/Applications/DrawBot.app" ]; then
    rm -rf "/Applications/DrawBot.app" 2>/dev/null || true
elif [ -f "/Applications/DrawBot.app" ]; then
    rm -f "/Applications/DrawBot.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.drawbot.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.drawbot.plist
echo "Removing $HOME/Library/Preferences/com.drawbot.plist..."
if [ -d "$HOME/Library/Preferences/com.drawbot.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.drawbot.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.drawbot.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.drawbot.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.drawbot.savedState
echo "Removing $HOME/Library/Saved Application State/com.drawbot.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.drawbot.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.drawbot.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.drawbot.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.drawbot.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
