#!/bin/bash
# Uninstall script for DriveDX
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DriveDX..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DriveDX if running..."
pkill -f "DriveDX" 2>/dev/null || true

# Remove /Applications/DriveDx.app
echo "Removing /Applications/DriveDx.app..."
if [ -d "/Applications/DriveDx.app" ]; then
    rm -rf "/Applications/DriveDx.app" 2>/dev/null || true
elif [ -f "/Applications/DriveDx.app" ]; then
    rm -f "/Applications/DriveDx.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
