#!/bin/bash
# Uninstall script for Dropshare
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Dropshare..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Dropshare if running..."
pkill -f "Dropshare" 2>/dev/null || true

# Remove /Applications/Dropshare 6.app
echo "Removing /Applications/Dropshare 6.app..."
if [ -d "/Applications/Dropshare 6.app" ]; then
    rm -rf "/Applications/Dropshare 6.app" 2>/dev/null || true
elif [ -f "/Applications/Dropshare 6.app" ]; then
    rm -f "/Applications/Dropshare 6.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
