#!/bin/bash
# Uninstall script for EasyFind
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling EasyFind..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping EasyFind if running..."
pkill -f "EasyFind" 2>/dev/null || true

# Remove /Applications/EasyFind.app
echo "Removing /Applications/EasyFind.app..."
if [ -d "/Applications/EasyFind.app" ]; then
    rm -rf "/Applications/EasyFind.app" 2>/dev/null || true
elif [ -f "/Applications/EasyFind.app" ]; then
    rm -f "/Applications/EasyFind.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
