#!/bin/bash
# Uninstall script for Elephas
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Elephas..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Elephas if running..."
