# Remove /Applications/Elephas.app
echo "Removing /Applications/Elephas.app..."
if [ -d "/Applications/Elephas.app" ]; then
    rm -rf "/Applications/Elephas.app" 2>/dev/null || true
elif [ -f "/Applications/Elephas.app" ]; then
    rm -f "/Applications/Elephas.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Elephas
