#!/bin/bash
# Uninstall script for Elgato Wave Link
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Elgato Wave Link..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Elgato Wave Link if running..."
pkill -f "Elgato Wave Link" 2>/dev/null || true

# Unload service com.elgato.WaveLink
echo "Unloading service com.elgato.WaveLink..."
launchctl unload -w /Library/LaunchAgents/com.elgato.WaveLink.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.elgato.WaveLink.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.elgato.WaveLink.plist 2>/dev/null || true

# Kill application with bundle ID com.elgato.WaveLink if running
echo "Stopping application with bundle ID com.elgato.WaveLink if running..."
killall -9 "com.elgato.WaveLink" 2>/dev/null || true

# Remove $HOME/Library/Logs/WaveLink
echo "Removing $HOME/Library/Logs/WaveLink..."
if [ -d "$HOME/Library/Logs/WaveLink" ]; then
    rm -rf "$HOME/Library/Logs/WaveLink" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/WaveLink" ]; then
    rm -f "$HOME/Library/Logs/WaveLink" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.elgato.WaveLink.plist
echo "Removing $HOME/Library/Preferences/com.elgato.WaveLink.plist..."
if [ -d "$HOME/Library/Preferences/com.elgato.WaveLink.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.elgato.WaveLink.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.elgato.WaveLink.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.elgato.WaveLink.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
