#!/bin/bash
# Uninstall script for Figma
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Figma..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Figma if running..."
pkill -f "Figma" 2>/dev/null || true

# Remove /Applications/Figma.app
echo "Removing /Applications/Figma.app..."
if [ -d "/Applications/Figma.app" ]; then
    rm -rf "/Applications/Figma.app" 2>/dev/null || true
elif [ -f "/Applications/Figma.app" ]; then
    rm -f "/Applications/Figma.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Figma
echo "Removing $HOME/Library/Application Support/Figma..."
if [ -d "$HOME/Library/Application Support/Figma" ]; then
    rm -rf "$HOME/Library/Application Support/Figma" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Figma" ]; then
    rm -f "$HOME/Library/Application Support/Figma" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/figma-desktop
echo "Removing $HOME/Library/Application Support/figma-desktop..."
if [ -d "$HOME/Library/Application Support/figma-desktop" ]; then
    rm -rf "$HOME/Library/Application Support/figma-desktop" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/figma-desktop" ]; then
    rm -f "$HOME/Library/Application Support/figma-desktop" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.figma.agent
echo "Removing $HOME/Library/Caches/com.figma.agent..."
