# Remove $HOME/Library/Caches/com.figma.Desktop
echo "Removing $HOME/Library/Caches/com.figma.Desktop..."
if [ -d "$HOME/Library/Caches/com.figma.Desktop" ]; then
    rm -rf "$HOME/Library/Caches/com.figma.Desktop" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.figma.Desktop" ]; then
    rm -f "$HOME/Library/Caches/com.figma.Desktop" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.figma.Desktop.plist
echo "Removing $HOME/Library/Preferences/com.figma.Desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.figma.Desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.figma.Desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.figma.Desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.figma.Desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.figma.Desktop.savedState
echo "Removing $HOME/Library/Saved Application State/com.figma.Desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.figma.Desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.figma.Desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.figma.Desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.figma.Desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
