#!/bin/bash
# Uninstall script for Fission
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fission..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fission if running..."
pkill -f "Fission" 2>/dev/null || true

# Remove /Applications/Fission.app
echo "Removing /Applications/Fission.app..."
if [ -d "/Applications/Fission.app" ]; then
    rm -rf "/Applications/Fission.app" 2>/dev/null || true
elif [ -f "/Applications/Fission.app" ]; then
    rm -f "/Applications/Fission.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
