#!/bin/bash
# Uninstall script for f.lux
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling f.lux..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping f.lux if running..."
pkill -f "f.lux" 2>/dev/null || true

# Remove /Applications/Flux.app
echo "Removing /Applications/Flux.app..."
if [ -d "/Applications/Flux.app" ]; then
    rm -rf "/Applications/Flux.app" 2>/dev/null || true
elif [ -f "/Applications/Flux.app" ]; then
    rm -f "/Applications/Flux.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
