#!/bin/bash
# Uninstall script for Foxit PDF Editor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Foxit PDF Editor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Foxit PDF Editor if running..."
pkill -f "Foxit PDF Editor" 2>/dev/null || true

# Unload service com.foxit.PDFEditorUpdateService
echo "Unloading service com.foxit.PDFEditorUpdateService..."
launchctl unload -w /Library/LaunchAgents/com.foxit.PDFEditorUpdateService.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.foxit.PDFEditorUpdateService.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.foxit.PDFEditorUpdateService.plist 2>/dev/null || true

# Remove $HOME/Library/Application Support/Foxit Software/Foxit PDF Editor
echo "Removing $HOME/Library/Application Support/Foxit Software/Foxit PDF Editor..."
if [ -d "$HOME/Library/Application Support/Foxit Software/Foxit PDF Editor" ]; then
    rm -rf "$HOME/Library/Application Support/Foxit Software/Foxit PDF Editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Foxit Software/Foxit PDF Editor" ]; then
    rm -f "$HOME/Library/Application Support/Foxit Software/Foxit PDF Editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.foxit-software.Foxit PDF Editor
echo "Removing $HOME/Library/Caches/com.foxit-software.Foxit PDF Editor..."
if [ -d "$HOME/Library/Caches/com.foxit-software.Foxit PDF Editor" ]; then
    rm -rf "$HOME/Library/Caches/com.foxit-software.Foxit PDF Editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.foxit-software.Foxit PDF Editor" ]; then
    rm -f "$HOME/Library/Caches/com.foxit-software.Foxit PDF Editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist
echo "Removing $HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist..."
if [ -d "$HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.foxit-software.Foxit PDF Editor.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
