#!/bin/bash
# Uninstall script for Free Ruler
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Free Ruler..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Free Ruler if running..."
pkill -f "Free Ruler" 2>/dev/null || true

# Remove /Applications/Free Ruler.app
echo "Removing /Applications/Free Ruler.app..."
if [ -d "/Applications/Free Ruler.app" ]; then
    rm -rf "/Applications/Free Ruler.app" 2>/dev/null || true
elif [ -f "/Applications/Free Ruler.app" ]; then
    rm -f "/Applications/Free Ruler.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.pascal.freeruler
echo "Removing $HOME/Library/Containers/com.pascal.freeruler..."
if [ -d "$HOME/Library/Containers/com.pascal.freeruler" ]; then
    rm -rf "$HOME/Library/Containers/com.pascal.freeruler" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.pascal.freeruler" ]; then
    rm -f "$HOME/Library/Containers/com.pascal.freeruler" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
