#!/bin/bash
# Uninstall script for FreeCAD
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling FreeCAD..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping FreeCAD if running..."
pkill -f "FreeCAD" 2>/dev/null || true

# Remove /Applications/FreeCAD.app
echo "Removing /Applications/FreeCAD.app..."
if [ -d "/Applications/FreeCAD.app" ]; then
    rm -rf "/Applications/FreeCAD.app" 2>/dev/null || true
elif [ -f "/Applications/FreeCAD.app" ]; then
    rm -f "/Applications/FreeCAD.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/FreeCAD
echo "Removing $HOME/Library/Application Support/FreeCAD..."
if [ -d "$HOME/Library/Application Support/FreeCAD" ]; then
    rm -rf "$HOME/Library/Application Support/FreeCAD" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/FreeCAD" ]; then
    rm -f "$HOME/Library/Application Support/FreeCAD" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/FreeCAD
echo "Removing $HOME/Library/Caches/FreeCAD..."
if [ -d "$HOME/Library/Caches/FreeCAD" ]; then
    rm -rf "$HOME/Library/Caches/FreeCAD" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/FreeCAD" ]; then
    rm -f "$HOME/Library/Caches/FreeCAD" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.freecad.FreeCAD.plist
echo "Removing $HOME/Library/Preferences/com.freecad.FreeCAD.plist..."
if [ -d "$HOME/Library/Preferences/com.freecad.FreeCAD.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.freecad.FreeCAD.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.freecad.FreeCAD.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.freecad.FreeCAD.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/FreeCAD
echo "Removing $HOME/Library/Preferences/FreeCAD..."
if [ -d "$HOME/Library/Preferences/FreeCAD" ]; then
    rm -rf "$HOME/Library/Preferences/FreeCAD" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/FreeCAD" ]; then
    rm -f "$HOME/Library/Preferences/FreeCAD" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
