#!/bin/bash
# Uninstall script for Freelens
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Freelens..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Freelens if running..."
pkill -f "Freelens" 2>/dev/null || true

# Remove /Applications/Freelens.app
echo "Removing /Applications/Freelens.app..."
if [ -d "/Applications/Freelens.app" ]; then
    rm -rf "/Applications/Freelens.app" 2>/dev/null || true
elif [ -f "/Applications/Freelens.app" ]; then
    rm -f "/Applications/Freelens.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Freelens
echo "Removing $HOME/Library/Application Support/Freelens..."
if [ -d "$HOME/Library/Application Support/Freelens" ]; then
    rm -rf "$HOME/Library/Application Support/Freelens" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Freelens" ]; then
    rm -f "$HOME/Library/Application Support/Freelens" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Freelens
echo "Removing $HOME/Library/Logs/Freelens..."
if [ -d "$HOME/Library/Logs/Freelens" ]; then
    rm -rf "$HOME/Library/Logs/Freelens" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Freelens" ]; then
    rm -f "$HOME/Library/Logs/Freelens" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
