#!/bin/bash
# Uninstall script for Geany
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Geany..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Geany if running..."
pkill -f "Geany" 2>/dev/null || true

# Remove /Applications/Geany.app
echo "Removing /Applications/Geany.app..."
if [ -d "/Applications/Geany.app" ]; then
    rm -rf "/Applications/Geany.app" 2>/dev/null || true
elif [ -f "/Applications/Geany.app" ]; then
    rm -f "/Applications/Geany.app" 2>/dev/null || true
fi

# Remove $HOME/.cache/geany
echo "Removing $HOME/.cache/geany..."
if [ -d "$HOME/.cache/geany" ]; then
    rm -rf "$HOME/.cache/geany" 2>/dev/null || true
elif [ -f "$HOME/.cache/geany" ]; then
    rm -f "$HOME/.cache/geany" 2>/dev/null || true
fi

# Remove $HOME/.config/geany
echo "Removing $HOME/.config/geany..."
if [ -d "$HOME/.config/geany" ]; then
    rm -rf "$HOME/.config/geany" 2>/dev/null || true
elif [ -f "$HOME/.config/geany" ]; then
    rm -f "$HOME/.config/geany" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
