#!/bin/bash
# Uninstall script for Geekbench AI
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Geekbench AI..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Geekbench AI if running..."
pkill -f "Geekbench AI" 2>/dev/null || true

# Remove /Applications/Geekbench AI.app
echo "Removing /Applications/Geekbench AI.app..."
if [ -d "/Applications/Geekbench AI.app" ]; then
    rm -rf "/Applications/Geekbench AI.app" 2>/dev/null || true
elif [ -f "/Applications/Geekbench AI.app" ]; then
    rm -f "/Applications/Geekbench AI.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
