#!/bin/bash
# Uninstall script for GitHub Desktop
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling GitHub Desktop..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping GitHub Desktop if running..."
pkill -f "GitHub Desktop" 2>/dev/null || true

# Remove /Applications/GitHub Desktop.app
echo "Removing /Applications/GitHub Desktop.app..."
if [ -d "/Applications/GitHub Desktop.app" ]; then
    rm -rf "/Applications/GitHub Desktop.app" 2>/dev/null || true
elif [ -f "/Applications/GitHub Desktop.app" ]; then
    rm -f "/Applications/GitHub Desktop.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
