#!/bin/bash
# Uninstall script for Godot Engine
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Godot Engine..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Godot Engine if running..."
pkill -f "Godot Engine" 2>/dev/null || true

# Remove /Applications/Godot.app
echo "Removing /Applications/Godot.app..."
if [ -d "/Applications/Godot.app" ]; then
    rm -rf "/Applications/Godot.app" 2>/dev/null || true
elif [ -f "/Applications/Godot.app" ]; then
    rm -f "/Applications/Godot.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
