#!/bin/bash
# Uninstall script for Google Ads Editor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Google Ads Editor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Google Ads Editor if running..."
pkill -f "Google Ads Editor" 2>/dev/null || true

# Remove /Applications/Google Ads Editor.app
echo "Removing /Applications/Google Ads Editor.app..."
if [ -d "/Applications/Google Ads Editor.app" ]; then
    rm -rf "/Applications/Google Ads Editor.app" 2>/dev/null || true
elif [ -f "/Applications/Google Ads Editor.app" ]; then
    rm -f "/Applications/Google Ads Editor.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.google.googleadseditor.savedState
echo "Removing $HOME/Library/Saved Application State/com.google.googleadseditor.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.google.googleadseditor.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.google.googleadseditor.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.google.googleadseditor.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.google.googleadseditor.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
