#!/bin/bash
# Uninstall script for Hammerspoon
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Hammerspoon..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Hammerspoon if running..."
pkill -f "Hammerspoon" 2>/dev/null || true

# Remove /Applications/Hammerspoon.app
echo "Removing /Applications/Hammerspoon.app..."
if [ -d "/Applications/Hammerspoon.app" ]; then
    rm -rf "/Applications/Hammerspoon.app" 2>/dev/null || true
elif [ -f "/Applications/Hammerspoon.app" ]; then
    rm -f "/Applications/Hammerspoon.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
